; INSERT INTO Patent (id, created_by, date_created, date_of_publication, date_patent_filed, description, inventors, last_updated, last_updated_by, patent_number, patent_status_id, patent_title, prior_entry, progress_report_id, baseline_date) VALUES (369, 1, CAST(N'2017-02-04T12:18:39.150' AS DateTime), CAST(N'2015-05-01T00:00:00.000' AS DateTime), CAST(N'2015-05-01T00:00:00.000' AS DateTime), N'Light-sheet fluorescence microscopy (LSFM) affords highly parallelized 3D imaging with optical sectioning capability and minimal light exposure. However, using Gaussian beams for light-sheet generation results in a trade-off between beam waist thickness and the area over which the beam can approximate a light-sheet. Novel techniques for LSFM are disclosed that uses extended focusing and/or laser line focuses to produce divergence free light-sheets with near diffraction-limited resolution and uniform intensity distribution.', N'Reto P. Fiolka, Kevin M. Dean', NULL, NULL, N'62/155980', 2, N'UNIFORM AND SCALABLE LIGHT-SHEETS GENERATED BY EXTENDED FOCUSING', 1, 5069, CAST(N'2019-04-30' AS Date))
