; INSERT INTO Patent (id, created_by, date_created, date_of_publication, date_patent_filed, description, inventors, last_updated, last_updated_by, patent_number, patent_status_id, patent_title, prior_entry, progress_report_id, baseline_date) VALUES (529, 211, CAST(N'2018-01-29T15:43:22.950' AS DateTime), NULL, CAST(N'2017-07-27T00:00:00.000' AS DateTime), N'Provided herein are methods and compositions for the identification of modulators of ApoE-induced LILRB activation. Also provided herein are methods of treating cancer comprising the administration of an inhibitor of ApoE-induced LILRB activation.  Also provided are methods of treating autoimmune disease or inhibiting the onset of transplant rejection or treating an inflammatory disorder comprising administering an agonist of ApoE-induced LILRB activation to a subject.', NULL, CAST(N'2018-01-29T15:43:22.950' AS DateTime), NULL, N'PCT/US2017/044171', 2, N'Methods to screen blocking antibodies against LILRBs', NULL, 5024, CAST(N'2019-04-30' AS Date))
