; INSERT INTO Patent (id, created_by, date_created, date_of_publication, date_patent_filed, description, inventors, last_updated, last_updated_by, patent_number, patent_status_id, patent_title, prior_entry, progress_report_id, baseline_date) VALUES (655, 1, CAST(N'2017-02-04T12:18:40.453' AS DateTime), CAST(N'2016-11-01T00:00:00.000' AS DateTime), CAST(N'2013-03-14T00:00:00.000' AS DateTime), N'Discloses apparatus for delivery of OCT imaging beam and ultrashort pulse TPL excitation beam onto tissue through a single optical fiber.  Patent application is relevant to CPRIT application in at least two respects. First, the methodology disclosed for combined OCT and TPL imaging may be applied and integrated with blood interruption and cutting beams as described in our CPRIT proposal.  A method and apparatus is envisioned where imaging is performed by either OCT or TPL modalities in addition to blood flow interruption and cutting.  Second, the apparatus disclosed in this patent application could accept blood flow interruption and cutting beams and deliver these onto the tissue through a single optical fiber.', NULL, CAST(N'2019-03-13T12:20:59.157' AS DateTime), 1577, N'US 9482513', 1, N'Apparatus and Methods for Optical Coherence Tomography and Two-Photon Luminescence Imaging', 1, 6256, CAST(N'2019-04-30' AS Date))
; INSERT INTO Patent (id, created_by, date_created, date_of_publication, date_patent_filed, description, inventors, last_updated, last_updated_by, patent_number, patent_status_id, patent_title, prior_entry, progress_report_id, baseline_date) VALUES (656, 1, CAST(N'2017-02-04T12:18:40.477' AS DateTime), CAST(N'2015-03-16T00:00:00.000' AS DateTime), CAST(N'2014-03-14T00:00:00.000' AS DateTime), N'Discloses apparatus for delivery of OCT imaging beam and ultrashort pulse TPL excitation beam onto tissue through a single optical fiber.  Patent application is relevant to CPRIT application in at least two respects. First, the methodology disclosed for combined OCT and TPL imaging may be applied and integrated with blood interruption and cutting beams as described in our CPRIT proposal.  A method and apparatus is envisioned where imaging is performed by either OCT or TPL modalities in addition to blood flow interruption and cutting.  Second, the apparatus disclosed in this patent application could accept blood flow interruption and cutting beams and deliver these onto the tissue through a single optical fiber.', N'Milner, TE, Feldman, MD, Wang, T and Phipps, J', NULL, NULL, N'PCT/US2014/028403', 2, N'“Apparatus and Methods for Optical Coherence Tomography and Two-Photon Luminescence Imaging”', 1, 6256, CAST(N'2019-04-30' AS Date))
; INSERT INTO Patent (id, created_by, date_created, date_of_publication, date_patent_filed, description, inventors, last_updated, last_updated_by, patent_number, patent_status_id, patent_title, prior_entry, progress_report_id, baseline_date) VALUES (657, 1, CAST(N'2017-02-04T12:18:40.500' AS DateTime), CAST(N'2012-01-12T00:00:00.000' AS DateTime), CAST(N'2011-06-10T00:00:00.000' AS DateTime), N'Published patent application discloses methods and devices for inducing a targeted phase-change at depth in tissues without modification of overlying tissue structures.  The published patent application is relevant to our CPRIT proposal considering that the disclosed methodology and/or apparatus would allow image-guided smart laser surgery at targeted depths in most tissues without modification of overlying structures.  Our CPRIT proposal describes a surface ablation methodology.  Methods and apparatus disclosed in the published patent application can be combined with imaging and blood flow interruption beams to realize an “image-guided subsurface smart laser knife” that would allow creation of targeted subsurface cuts without modification of the tissue surface.  An “image-guided subsurface smart laser knife” may have application to basal cell carcinoma or breast cancer so that the malignant tissue may be removed without compromising the most superficial layers of skin.', NULL, CAST(N'2017-12-06T10:03:05.260' AS DateTime), 1577, N'20120010603', 2, N'TISSUE OPTICAL CLEARING DEVICES FOR SUBSURFACE LIGHT-INDUCED PHASE-CHANGE AND METHOD OF USE', 1, 6256, CAST(N'2019-04-30' AS Date))
