; INSERT INTO Patent (id, created_by, date_created, date_of_publication, date_patent_filed, description, inventors, last_updated, last_updated_by, patent_number, patent_status_id, patent_title, prior_entry, progress_report_id, baseline_date) VALUES (1024, 2780, CAST(N'2020-04-16T16:34:59.287' AS DateTime), CAST(N'2019-05-12T00:00:00.000' AS DateTime), CAST(N'2019-06-01T00:00:00.000' AS DateTime), N'The invention provides a method for coating enzymes in nanoporous silica that allows free access to small molecules substrates, but not larger molecules such as antibodies or immune cells to be used as a treatment or imaging tool without interacting with the immune system. This approach extends the enzyme''s activity in vivo and limits or prevents immune reactions.', NULL, CAST(N'2020-04-16T16:34:59.287' AS DateTime), NULL, N'US20190365869', 2, N'BIORESPONSIVE PARTICLES', NULL, 6961, CAST(N'2020-08-31' AS Date))
