; INSERT INTO Patent (id, created_by, date_created, date_of_publication, date_patent_filed, description, inventors, last_updated, last_updated_by, patent_number, patent_status_id, patent_title, prior_entry, progress_report_id, baseline_date) VALUES (1061, 2043, CAST(N'2020-07-26T13:22:31.470' AS DateTime), CAST(N'2020-04-17T00:00:00.000' AS DateTime), CAST(N'2020-04-17T00:00:00.000' AS DateTime), N'Potent thrombin inhibitors identified from a DEC-Tec library', NULL, CAST(N'2020-07-26T13:22:31.470' AS DateTime), NULL, N'63/011,439', 2, N'DIRECT THROMBIN INHIBITORS AND METHODS OF USE THEREOF', 1, 7881, CAST(N'2021-11-30' AS Date))
; INSERT INTO Patent (id, created_by, date_created, date_of_publication, date_patent_filed, description, inventors, last_updated, last_updated_by, patent_number, patent_status_id, patent_title, prior_entry, progress_report_id, baseline_date) VALUES (1062, 1537, CAST(N'2020-08-14T11:49:25.307' AS DateTime), NULL, CAST(N'2019-04-29T00:00:00.000' AS DateTime), N'A research group used the core’s pay-for-service to utilize the single-cell assay platforms  to examine the expression of gap junction genes in endometriosis and non-endometriosis eutopic samples.  This gene panel includes: 13 of the total of 21 connexins (Cx) encoded by GJ genes, including the major expressers in the endometrium, Cx43 (GJA1), 26 (GJB2) and 32 (GJB1), and at least one from each GJ class (A, B and C); as well as ancillary factors such as CDH2, MAPK , CK1, SRC,  CDK1, CAV1. An inverse patterns are observed in the ESC and EEC compartments in endometriosis (relative to control) – i.e., decreased and increased GJ gene expression, respectively. This gene panel may therefore be used for the diagnosis of endometriosis.', NULL, CAST(N'2020-08-14T11:49:25.307' AS DateTime), NULL, N'PCT/US2019/028478', 2, N'Non-invasive Diagnosis of Endometriosis Via Single-Cell Expression of Communicome Genes from Menstrual Flow', NULL, 7085, CAST(N'2020-11-30' AS Date))
; INSERT INTO Patent (id, created_by, date_created, date_of_publication, date_patent_filed, description, inventors, last_updated, last_updated_by, patent_number, patent_status_id, patent_title, prior_entry, progress_report_id, baseline_date) VALUES (1063, 2089, CAST(N'2017-07-14T10:19:36.577' AS DateTime), NULL, CAST(N'2020-12-21T00:00:00.000' AS DateTime), N'In fluorescence microscopy, a 3D volume is normally imaged by acquiring a series of 2D images where the focal position of the detection is changed. This serial acquisition slows down the acquisition rate and reduces sensitivity. Here we present a way to image multiple focal planes separately and simultaneously while minimizing light losses. The invention can be used to image fluorescently labeled cells and small organisms cultured on glass coverslips or other planar surfaces.', NULL, CAST(N'2021-02-26T17:47:53.457' AS DateTime), 1615, N'17/135,679', 2, N'Light-Sheet Microscope with Parallelized 3D Image Acquisition', 1, 7883, CAST(N'2021-05-31' AS Date))
; INSERT INTO Patent (id, created_by, date_created, date_of_publication, date_patent_filed, description, inventors, last_updated, last_updated_by, patent_number, patent_status_id, patent_title, prior_entry, progress_report_id, baseline_date) VALUES (1064, 2089, CAST(N'2017-07-14T10:20:53.440' AS DateTime), CAST(N'2021-02-02T00:00:00.000' AS DateTime), CAST(N'2016-12-15T00:00:00.000' AS DateTime), N'Total internal reflection fluorescence microscopy is a widely used technique to selectively image fluorescent proteins adjacent to a glass cover slip. Its unrivaled sensitivity and signal to noise ratio have enabled the observation of endocytois, exocytosis, trafficking and virus dynamics. TIRF is based on total internal reflection and the resulting evanescent field that is generated at a refractive index interface (here glass to water). The evanescent field only penetrates around 100nm into the sample, creating a very thin illuminated optical section. In practice, the evanescent field is however scattered by the sample, resulting in out-of-focus blur and increased background. By design of a TIRF microscope, there is no mechanism to reject such blur, as all fluorescence light emitted by the microscope is captured. The invention here presents a method to reject out of focus blur in TIRF microscopy, thereby greatly increasing the clarity and signal to noise ratio in the final image. It is expected that this invention will greatly aid researchers to image weakly fluorescent objects and aid image analysis.', NULL, CAST(N'2021-02-26T17:41:38.063' AS DateTime), 1615, N'US20180172578A1', 1, N'Total Internal Reflection and Transmission Illumination Fluorescence Microscopy Imaging System with Improved Background Suppression', 1, 7883, CAST(N'2021-05-31' AS Date))
; INSERT INTO Patent (id, created_by, date_created, date_of_publication, date_patent_filed, description, inventors, last_updated, last_updated_by, patent_number, patent_status_id, patent_title, prior_entry, progress_report_id, baseline_date) VALUES (1065, 2089, CAST(N'2019-07-12T11:07:33.010' AS DateTime), NULL, CAST(N'2018-08-13T00:00:00.000' AS DateTime), N'There are multiple varieties of light-sheet microscopy, mainly differing in the way the light-sheets are generated (Gaussian beams, Bessel beams, Airy beams, Lattice beams, etc.). These different light-sheet microscopes serve different purposes, ranging from whole embryo imaging to high resolution, subcellular imaging. They are complementary and there is no one size fits it all. We discovered a new mathematical theory that describes how all aforementioned light-sheets can be synthesized. This led us to a technique that we termed field synthesis light-sheet microscopy that can generate various light-sheets on demand. It is more flexible than previous technologies and thus could replace the different light-sheet microscopy implementations with a single system. Its simpler optical design also equates to significant cost savings for such an instrument. We also expect that our field synthesis light-sheet approach is less photo-toxic to fluorescently labeled samples.', NULL, CAST(N'2019-07-12T11:07:33.010' AS DateTime), NULL, N'62/718,325', 2, N'FLEXIBLE LIGHT SHEET GENERATION BY FIELD SYNTHESIS', 1, 7883, CAST(N'2021-05-31' AS Date))
; INSERT INTO Patent (id, created_by, date_created, date_of_publication, date_patent_filed, description, inventors, last_updated, last_updated_by, patent_number, patent_status_id, patent_title, prior_entry, progress_report_id, baseline_date) VALUES (1066, 2089, CAST(N'2020-06-23T21:16:43.680' AS DateTime), NULL, CAST(N'2020-02-25T00:00:00.000' AS DateTime), N'Embodiments transform a lateral scan motion into axial refocusing, thus enabling aberration free remote focusing.  In one embodiment of an imaging system providing aberration free remote focusing, a beam of light is scanned over a stationary mirror and the height of the mirror varies in the scan direction, which defocuses the beam of light in an axial direction in the return direction (e.g., the direction of propagation for the reflected beam of light).  If the back-reflected light resulting from the lateral scan component is configured as described herein, a pure axial scan motion may be obtained at the rate of the employed (lateral) scanning technology (e.g., a GSM).  This capability allows the imaging system to leverage rapid lateral scan technologies to produce rapid axial displacement of the axial focus of the beam of light.', NULL, CAST(N'2020-06-23T21:16:43.680' AS DateTime), NULL, N'No. 62/981,489', 2, N'CONVERTING LATERAL SCANNING INTO AXIAL REMOTE FOCUSING', 1, 7883, CAST(N'2021-05-31' AS Date))
; INSERT INTO Patent (id, created_by, date_created, date_of_publication, date_patent_filed, description, inventors, last_updated, last_updated_by, patent_number, patent_status_id, patent_title, prior_entry, progress_report_id, baseline_date) VALUES (1067, 2361, CAST(N'2017-07-27T16:51:55.437' AS DateTime), CAST(N'2017-07-09T00:00:00.000' AS DateTime), CAST(N'2017-07-09T00:00:00.000' AS DateTime), N'TNFRSF25 epitope mapping and PTX-25 variable binding region affinity maturation program for improved functional properties and to add to a dominant IP position covering the receptor’s key amino acids for agonist binding as well as an antibody’s functional agonist activity.', NULL, CAST(N'2017-07-27T16:51:55.437' AS DateTime), NULL, N'PCT/US2017/036817', 2, N'ANTI-TNFRSF25 ANTIBODIES', 1, 7886, CAST(N'2021-08-31' AS Date))
