; INSERT INTO Patent (id, created_by, date_created, date_of_publication, date_patent_filed, description, inventors, last_updated, last_updated_by, patent_number, patent_status_id, patent_title, prior_entry, progress_report_id, baseline_date) VALUES (1360, 1599, CAST(N'2021-09-13T17:32:16.220' AS DateTime), NULL, CAST(N'2019-02-28T00:00:00.000' AS DateTime), N'Described herein are compounds and pharmaceutical compositions containing such compounds, which inhibit the activity of Olig2.  Also described herein are methods of using such Olig2 inhibitors, alone and in combination with other compounds, for treating cancer and other diseases.  In particular the Olig2 inhibitors may be used to treat glioblastoma.', NULL, CAST(N'2021-09-13T17:32:54.160' AS DateTime), 1599, N'PCT/US2019/020016', 2, N'INHIBITION OF OLIG2 ACTIVITY', NULL, 8733, CAST(N'2021-11-30' AS Date))
; INSERT INTO Patent (id, created_by, date_created, date_of_publication, date_patent_filed, description, inventors, last_updated, last_updated_by, patent_number, patent_status_id, patent_title, prior_entry, progress_report_id, baseline_date) VALUES (1361, 2533, CAST(N'2021-09-17T00:03:34.807' AS DateTime), CAST(N'2021-09-01T00:00:00.000' AS DateTime), CAST(N'2021-09-01T00:00:00.000' AS DateTime), N'Neoantigens play a key role in the recognition of tumor cells by T cells. However, only a small proportion of neoantigens truly elicit T cell responses, and fewer clues exist as to which neoantigens are recognized by which T cell receptors (TCRs) of the T cells. To address this significant issue, we built a transfer learning-based model, pMTnet, to predict TCR-binding specificities of neoantigens presented by class I MHCs. pMTnet achieved an AUC of 0.833 on an independent validation cohort, was comprehensively validated by many other external validation datasets, and showed significant advance over previous works.', NULL, CAST(N'2021-09-17T00:03:34.807' AS DateTime), NULL, N'UTSD 3738', 2, N'pMTnet: A deep learning tool for predicting the T cell receptor binding specificity of neoantigens', NULL, 8191, CAST(N'2021-11-30' AS Date))
; INSERT INTO Patent (id, created_by, date_created, date_of_publication, date_patent_filed, description, inventors, last_updated, last_updated_by, patent_number, patent_status_id, patent_title, prior_entry, progress_report_id, baseline_date) VALUES (1362, 2439, CAST(N'2021-09-27T12:49:01.873' AS DateTime), CAST(N'2021-05-26T00:00:00.000' AS DateTime), CAST(N'2021-05-26T00:00:00.000' AS DateTime), N'Provisional Patent Refiling TAMU-5243', NULL, CAST(N'2021-09-27T12:49:01.873' AS DateTime), NULL, N'Serial No. 63/193,229', 2, N'Biodegradable Mucoadhesive Delivery Patches and Methods Thereof', NULL, 8100, CAST(N'2021-11-30' AS Date))
; INSERT INTO Patent (id, created_by, date_created, date_of_publication, date_patent_filed, description, inventors, last_updated, last_updated_by, patent_number, patent_status_id, patent_title, prior_entry, progress_report_id, baseline_date) VALUES (1363, 2470, CAST(N'2021-09-27T14:44:41.757' AS DateTime), NULL, CAST(N'2021-04-23T00:00:00.000' AS DateTime), N'There have been extraordinary successes with CAR-T cell therapy for hematological malignancies. Broadening the application of CAR-T cells to include solid tumors as targets, however, has proven more challenging. One of the main obstacles  to overcome for this therapy to be successful in solid tumors is the hostile tumor microenvironment (TME). The TME is composed of cells and secreted cytokines that are potently immunosuppressive and block the activity of CART cells. In many solid tumors, myeloid derived suppressor cells (MDSCs) are the main contributors to induction and maintenance of T cell suppression. the accumulation of MDSCs at the tumor site has been associated with poor prognosis, increased disease burden and importantly, poor response to immunotherapies. Armed with these facts, investigators have focused on finding ways to eliminate MDSCs from the TME. Most strategies are non-specific and have significant side effects. However, a targeted approach is to use an agonist mAb against TNF-related apoptosis induced ligand-receptor 2 (TRAIL-R2); a receptor expressed on MDSCs that activates apoptosis upon engagement with its soluble ligands. Our idea and approach was to engineer a receptor that engages with TRAIL-R2, therefore inducing apoptosis of MDSCs and at the same time provides a co-stimulatory signal to CART cells that improve their persistence and anti-tumor effects. For this receptor we used the scFv of a monoclonal antibody specific for TRAIL-R2 as the extracellular domain of the receptor and as signaling the intracellular domain of 4-1BB. We call this receptor TR2-41BB.  T cells engineered to express TR2-41BB are able to specifically eliminate MDSCs in vitro and in vivo show improved anti-tumor effects as well as higher proliferation and persistance at the tumor site compared to CART cells alone.', NULL, CAST(N'2021-09-27T14:44:41.757' AS DateTime), NULL, N'PCT/US2021/028437', 2, N'OVERCOMING THE TUMOR MICROENVIRONMENT FOR CELL THERAPY BY TARGETING MYELOID DERIVED SUPPRESSOR CELLS THROUGH A TRAIL-R2 SPECIFIC RECEPTOR', NULL, 8089, CAST(N'2021-11-30' AS Date))
; INSERT INTO Patent (id, created_by, date_created, date_of_publication, date_patent_filed, description, inventors, last_updated, last_updated_by, patent_number, patent_status_id, patent_title, prior_entry, progress_report_id, baseline_date) VALUES (1364, 1537, CAST(N'2020-08-14T11:49:25.307' AS DateTime), NULL, CAST(N'2019-04-29T00:00:00.000' AS DateTime), N'A research group used the core’s pay-for-service to utilize the single-cell assay platforms  to examine the expression of gap junction genes in endometriosis and non-endometriosis eutopic samples.  This gene panel includes: 13 of the total of 21 connexins (Cx) encoded by GJ genes, including the major expressers in the endometrium, Cx43 (GJA1), 26 (GJB2) and 32 (GJB1), and at least one from each GJ class (A, B and C); as well as ancillary factors such as CDH2, MAPK , CK1, SRC,  CDK1, CAV1. An inverse patterns are observed in the ESC and EEC compartments in endometriosis (relative to control) – i.e., decreased and increased GJ gene expression, respectively. This gene panel may therefore be used for the diagnosis of endometriosis.', NULL, CAST(N'2020-08-14T11:49:25.307' AS DateTime), NULL, N'PCT/US2019/028478', 2, N'Non-invasive Diagnosis of Endometriosis Via Single-Cell Expression of Communicome Genes from Menstrual Flow', 1, 8832, CAST(N'2022-05-31' AS Date))
