; INSERT INTO Patent (id, created_by, date_created, date_of_publication, date_patent_filed, description, inventors, last_updated, last_updated_by, patent_number, patent_status_id, patent_title, prior_entry, progress_report_id, baseline_date) VALUES (1378, 1562, CAST(N'2021-10-26T13:03:10.420' AS DateTime), CAST(N'2021-07-13T00:00:00.000' AS DateTime), CAST(N'2021-07-30T00:00:00.000' AS DateTime), N'Antibodies targeting OSMR for potential treatment of ovarian cancer', NULL, CAST(N'2021-10-26T13:03:10.420' AS DateTime), NULL, N'63/228,005', 2, N'ONCOSTATIN M RECEPTOR SPECIFIC MONOCLONAL ANTIBODIES AND THEIR METHODS FOR USE', NULL, 8108, CAST(N'2021-11-30' AS Date))
; INSERT INTO Patent (id, created_by, date_created, date_of_publication, date_patent_filed, description, inventors, last_updated, last_updated_by, patent_number, patent_status_id, patent_title, prior_entry, progress_report_id, baseline_date) VALUES (1379, 2470, CAST(N'2021-09-27T14:44:41.757' AS DateTime), NULL, CAST(N'2021-04-23T00:00:00.000' AS DateTime), N'There have been extraordinary successes with CAR-T cell therapy for hematological malignancies. Broadening the application of CAR-T cells to include solid tumors as targets, however, has proven more challenging. One of the main obstacles  to overcome for this therapy to be successful in solid tumors is the hostile tumor microenvironment (TME). The TME is composed of cells and secreted cytokines that are potently immunosuppressive and block the activity of CART cells. In many solid tumors, myeloid derived suppressor cells (MDSCs) are the main contributors to induction and maintenance of T cell suppression. the accumulation of MDSCs at the tumor site has been associated with poor prognosis, increased disease burden and importantly, poor response to immunotherapies. Armed with these facts, investigators have focused on finding ways to eliminate MDSCs from the TME. Most strategies are non-specific and have significant side effects. However, a targeted approach is to use an agonist mAb against TNF-related apoptosis induced ligand-receptor 2 (TRAIL-R2); a receptor expressed on MDSCs that activates apoptosis upon engagement with its soluble ligands. Our idea and approach was to engineer a receptor that engages with TRAIL-R2, therefore inducing apoptosis of MDSCs and at the same time provides a co-stimulatory signal to CART cells that improve their persistence and anti-tumor effects. For this receptor we used the scFv of a monoclonal antibody specific for TRAIL-R2 as the extracellular domain of the receptor and as signaling the intracellular domain of 4-1BB. We call this receptor TR2-41BB.  T cells engineered to express TR2-41BB are able to specifically eliminate MDSCs in vitro and in vivo show improved anti-tumor effects as well as higher proliferation and persistance at the tumor site compared to CART cells alone.', NULL, CAST(N'2021-09-27T14:44:41.757' AS DateTime), NULL, N'PCT/US2021/028437', 2, N'OVERCOMING THE TUMOR MICROENVIRONMENT FOR CELL THERAPY BY TARGETING MYELOID DERIVED SUPPRESSOR CELLS THROUGH A TRAIL-R2 SPECIFIC RECEPTOR', 1, 8878, CAST(N'2022-11-30' AS Date))
; INSERT INTO Patent (id, created_by, date_created, date_of_publication, date_patent_filed, description, inventors, last_updated, last_updated_by, patent_number, patent_status_id, patent_title, prior_entry, progress_report_id, baseline_date) VALUES (1380, 3012, CAST(N'2021-10-26T17:25:26.417' AS DateTime), CAST(N'2021-07-20T00:00:00.000' AS DateTime), CAST(N'2017-01-26T00:00:00.000' AS DateTime), N'Systems, methods, and computer-readable storage media providing techniques for probing in-vivo beam ranges directly using therapeutic beams for particle therapy treatment are disclosed. In an embodiment, a configuration is determined for one or more probing spots, each spot corresponding to a planned location within an interior region of a tumor volume where a dose of radiation is to be delivered. At least one therapeutic beam is provided to the tumor volume, and one or more images may be captured to provide an indication of the range/depth of the probing spots. Providing the probing spots to the interior of the tumor volume reduces the risk that the dose is provided to sensitive tissue (e.g., because even if the dose is delivered to a location other than the planned location, the dose is likely to remain contained within the tumor volume).', NULL, CAST(N'2021-10-26T17:25:26.417' AS DateTime), NULL, N'US11065476B2', 2, N'Mid-plane range-probing techniques for particle therapy', NULL, 8313, CAST(N'2021-11-30' AS Date))
