; INSERT INTO Patent (id, created_by, date_created, date_of_publication, date_patent_filed, description, inventors, last_updated, last_updated_by, patent_number, patent_status_id, patent_title, prior_entry, progress_report_id, baseline_date) VALUES (1403, 2383, CAST(N'2020-10-19T10:26:39.690' AS DateTime), NULL, CAST(N'2020-08-06T00:00:00.000' AS DateTime), N'Methods of generating an isolated population of non-graft versus host disease (GVHD) inducing cells comprising a central memory T-lymphocyte (Tcm) phenotype, the cells being tolerance inducing cells and/or endowed with anti-disease activity, and capable of homing to the lymph nodes following transplantation, are disclosed.  Cells generated by the methods, pharmaceutical compositions and methods of treatment are also disclosed.', NULL, CAST(N'2020-10-19T10:26:39.690' AS DateTime), NULL, N'PCT/IL2020/050865', 2, N'ANTI-VIRAL CENTRAL MEMORY CD8 VETO CELLS IN HAPLOIDENTICAL STEM CELL TRANSPLANTATION', 1, 8927, CAST(N'2022-11-30' AS Date))
; INSERT INTO Patent (id, created_by, date_created, date_of_publication, date_patent_filed, description, inventors, last_updated, last_updated_by, patent_number, patent_status_id, patent_title, prior_entry, progress_report_id, baseline_date) VALUES (1404, 1477, CAST(N'2021-10-22T17:06:20.270' AS DateTime), CAST(N'2021-02-13T00:00:00.000' AS DateTime), CAST(N'2021-02-13T00:00:00.000' AS DateTime), N'Hyperspectral Endoscopic Imaging System', NULL, CAST(N'2021-10-22T17:06:20.270' AS DateTime), NULL, N'63/149,255', 2, N'Hyperspectral Endoscopic Imaging System', 1, 8931, CAST(N'2023-02-28' AS Date))
; INSERT INTO Patent (id, created_by, date_created, date_of_publication, date_patent_filed, description, inventors, last_updated, last_updated_by, patent_number, patent_status_id, patent_title, prior_entry, progress_report_id, baseline_date) VALUES (1405, 1445, CAST(N'2020-10-08T16:56:24.873' AS DateTime), CAST(N'2019-12-10T00:00:00.000' AS DateTime), CAST(N'2020-05-07T00:00:00.000' AS DateTime), N'CD8 T cells and natural killer (NK) cells, central cellular components of immune responses against pathogens and cancer, rely on IL-15 for homeostasis. Here we show that IL-15 also mediates homeostatic priming of CD8 T cells for antigen-stimulated activation, which is controlled by a deubiquitinase, Otub1. Otub1 deficiency greatly enhances responses of CD8 T cells to IL-15, rendering naïve CD8 T cells hyper-sensitive to antigen stimulation characterized by enhanced metabolic reprograming and effector functions. Otub1 also controls the maturation and activation of NK cells. Importantly, Otub1 deletion profoundly enhances anticancer immunity in mouse models of cancer immunotherapy. Using a T cell adoptive therapy model, we further show that Otub1 deletion is able to substantially improve the efficacy of cancer treatment based on autologous T cell transfer. Our findings suggest that Otub1 controls the activation of CD8 T cells and NK cells by functioning as a checkpoint of IL-15-mediated priming, establishing Otub1 as a new target for cancer immunotherapy. Different approaches of Otub1 targeting will be employed for cancer immunotherapy.', NULL, CAST(N'2020-10-08T16:56:24.873' AS DateTime), NULL, N'PCT/US2020/031816', 2, N'Targeting OTUB1 In Immunotherapy', 1, 8934, CAST(N'2022-08-31' AS Date))
; INSERT INTO Patent (id, created_by, date_created, date_of_publication, date_patent_filed, description, inventors, last_updated, last_updated_by, patent_number, patent_status_id, patent_title, prior_entry, progress_report_id, baseline_date) VALUES (1406, 2533, CAST(N'2021-09-17T00:03:34.807' AS DateTime), CAST(N'2021-09-01T00:00:00.000' AS DateTime), CAST(N'2021-09-01T00:00:00.000' AS DateTime), N'Neoantigens play a key role in the recognition of tumor cells by T cells. However, only a small proportion of neoantigens truly elicit T cell responses, and fewer clues exist as to which neoantigens are recognized by which T cell receptors (TCRs) of the T cells. To address this significant issue, we built a transfer learning-based model, pMTnet, to predict TCR-binding specificities of neoantigens presented by class I MHCs. pMTnet achieved an AUC of 0.833 on an independent validation cohort, was comprehensively validated by many other external validation datasets, and showed significant advance over previous works.', NULL, CAST(N'2021-09-17T00:03:34.807' AS DateTime), NULL, N'UTSD 3738', 2, N'pMTnet: A deep learning tool for predicting the T cell receptor binding specificity of neoantigens', 1, 8964, CAST(N'2022-11-30' AS Date))
; INSERT INTO Patent (id, created_by, date_created, date_of_publication, date_patent_filed, description, inventors, last_updated, last_updated_by, patent_number, patent_status_id, patent_title, prior_entry, progress_report_id, baseline_date) VALUES (1407, 3346, CAST(N'2021-10-08T09:24:40.427' AS DateTime), CAST(N'2021-06-25T00:00:00.000' AS DateTime), CAST(N'2021-06-25T00:00:00.000' AS DateTime), N'Describes novel chemical entities with capability of inhibiting HAT1 enzymatic activity', NULL, CAST(N'2021-10-08T09:24:40.427' AS DateTime), NULL, N'63215322', 2, N'MODULATORS OF HISTONE ACETYLTRANSFERASE 1 AND METHODS OF TREATMENT THEREOF', 1, 8983, CAST(N'2022-11-30' AS Date))
; INSERT INTO Patent (id, created_by, date_created, date_of_publication, date_patent_filed, description, inventors, last_updated, last_updated_by, patent_number, patent_status_id, patent_title, prior_entry, progress_report_id, baseline_date) VALUES (1408, 2498, CAST(N'2020-10-16T17:57:51.350' AS DateTime), NULL, CAST(N'2020-03-16T00:00:00.000' AS DateTime), N'[5]   The present disclosure provides an improved computerized framework for antigen-independent de novo prediction of cancer-associated TCR repertoire. The disclosed framework is a pan-cancer early detection tool that is able to augment the small signals emitted from early stage cancer by analyzing and understanding the changes in the blood T cell repertoire. The disclosed systems and methods provide for the ability to detect, at the earliest stages, cancers that many current technologies are unable to identify – for example, kidney cancer, ovarian cancer and pancreatic cancer. As discussed herein, in addition to the improved capabilities for early-stage cancer detection, the disclosed framework provides capabilities for improving the accuracy of detecting late-stage cancer in patients, as, for example, it can be used together with radiographic images to increase their diagnostic accuracy (in addition to the existing traditional methods mentioned above).
[6]     The disclosed systems and methods embody the first immune-based cancer detection techniques or technology. That is, when an individual has cancer, the immune system will react by proliferation of cancer-specific T cells and circulate them in the blood and lymph system. While this bodily reaction is naturally occurring, its presentation in, and the analysis of blood data is not, and thus an improved automated framework is necessary to perform such analysis.  The disclosed framework uses a specific automation technique to detect cancer signals from the signatures of the peripheral immune repertoire, which can be performed with higher accuracy than present automated methodologies even at the early stages of the disease.
[7]     According to some embodiments of the instant disclosure, the disclosed framework executes a novel machine learning algorithm that can predict cancer status based on a patient’s peripheral blood TCR repertoire. As discussed in more detail below, starting with a normal amount of blood sample (e.g., 3-10 ml), the disclosed framework can perform deep TCR sequencing of the genomic DNA of the white blood cells, which enables the detection (prediction or determination) of cancer-associated TCRs independent of tumor antigens. This is then leveraged in order to identify a patient’s “cancer score”, which is reflective of their immune repertoire. The score is an output of an automated process which output represents a robust biomarker for both early and late-stage cancers across diverse diseases, and is predictive of patient response to checkpoint blockade therapies. Thus, the determined score is a strong indicator of whether a patient has cancer, and to what degree.
[8]     In accordance with one or more embodiments, the instant disclosure provides computerized methods for a novel framework for diagnosing cancer status with peripheral blood TCR repertoire. In accordance with one or more embodiments, the instant disclosure provides a non-transitory computer-readable storage medium for carrying out the above mentioned technical steps of the framework’s functionality. The non-transitory computer-readable storage medium has tangibly stored thereon, or tangibly encoded thereon, computer readable instructions that when executed by a device cause at least one processor to perform a method for a novel and improved framework for diagnosing cancer status with peripheral blood TCR repertoire.
[9]     In accordance with one or more embodiments, a system is provided that comprises one or more computing devices configured to provide functionality in accordance with such embodiments.  In accordance with one or more embodiments, functionality is embodied in steps of a method performed by at least one computing device.  In accordance with one or more embodiments, program code (or program logic) executed by a processor(s) of a computing device to implement functionality in accordance with one or more such embodiments is embodied in, by and/or on a non-transitory computer-readable medium.', NULL, CAST(N'2022-12-07T12:12:01.270' AS DateTime), 2206, N'133892-020601/PCT', 2, N'Computerized System and Method for Antigen-Independent De Novo Predication
of Cancer-Associated TCR Repertoire', 1, 8988, CAST(N'2023-02-28' AS Date))
