; INSERT INTO Patent (id, created_by, date_created, date_of_publication, date_patent_filed, description, inventors, last_updated, last_updated_by, patent_number, patent_status_id, patent_title, prior_entry, progress_report_id, baseline_date) VALUES (1542, 2383, CAST(N'2022-10-14T15:31:39.003' AS DateTime), CAST(N'2021-12-08T00:00:00.000' AS DateTime), CAST(N'2021-12-08T00:00:00.000' AS DateTime), N'A method of generating an isolated population of pulmonary cells is disclosed. The method comprising: (a) dissociating a pulmonary tissue so as to obtain a population of isolated pulmonary cells; and (b) contacting the population of isolated pulmonary cells with at least one agent capable of binding an epithelial cell marker and an endothelial cell marker, so as to select a cell population being double positive for expression of epithelial and endothelial cell markers, thereby generating the isolated population of pulmonary cells. An isolated population of pulmonary cells, pharmaceutical compositions comprising the pulmonary cells, use of the pulmonary cells in treatment, kits for isolation of the cells and a cell bank are also disclosed.', NULL, CAST(N'2022-10-14T15:31:39.003' AS DateTime), NULL, N'63/287,147', 2, N'MULTI-POTENT LUNG STEM CELLS FOR LUNG REGENERATION', NULL, 8927, CAST(N'2022-11-30' AS Date))
