; INSERT INTO Patent (id, created_by, date_created, date_of_publication, date_patent_filed, description, inventors, last_updated, last_updated_by, patent_number, patent_status_id, patent_title, prior_entry, progress_report_id, baseline_date) VALUES (1767, 2533, CAST(N'2021-09-17T00:03:34.807' AS DateTime), CAST(N'2021-09-01T00:00:00.000' AS DateTime), CAST(N'2021-09-01T00:00:00.000' AS DateTime), N'Neoantigens play a key role in the recognition of tumor cells by T cells. However, only a small proportion of neoantigens truly elicit T cell responses, and fewer clues exist as to which neoantigens are recognized by which T cell receptors (TCRs) of the T cells. To address this significant issue, we built a transfer learning-based model, pMTnet, to predict TCR-binding specificities of neoantigens presented by class I MHCs. pMTnet achieved an AUC of 0.833 on an independent validation cohort, was comprehensively validated by many other external validation datasets, and showed significant advance over previous works.', NULL, CAST(N'2021-09-17T00:03:34.807' AS DateTime), NULL, N'UTSD 3738', 2, N'pMTnet: A deep learning tool for predicting the T cell receptor binding specificity of neoantigens', 1, 10739, NULL)
