; INSERT INTO Patent (id, created_by, date_created, date_of_publication, date_patent_filed, description, inventors, last_updated, last_updated_by, patent_number, patent_status_id, patent_title, prior_entry, progress_report_id, baseline_date) VALUES (1791, 3783, CAST(N'2022-10-17T15:27:50.350' AS DateTime), CAST(N'2022-06-03T00:00:00.000' AS DateTime), CAST(N'2022-02-15T00:00:00.000' AS DateTime), N'Adoptive transfer of expanded, tumor antigen-specific T cells represents an appealing approach to treating various cancers. The process for generating tumor antigen-specific T cells having the desired activity and phenotype, at the required scale for clinical use, is a challenge. In particular, the length of time required to generate tumor antigen-specific T cells for use in the clinic can be detrimental to patient care. There is a need in the art for improved methods for generating tumor antigen-specific T cells that can be administered to patients for treatment of cancers. There is also a need in the art for improved tumor antigen-specific T cell products which can be used to effectively treat patients.', NULL, CAST(N'2022-10-17T15:27:50.350' AS DateTime), NULL, N'63/310,500', 2, N'COMPOSITIONS AND METHODS FOR ANTIGEN-SPECIFIC T CELL EXPANSION', 1, 10900, NULL)
